package main

import "fmt"

/**
* @Description
* idea: i < j (nums1[i]-nums2[i]) - (nums1[j]-nums2[j]) <= dif
  易错点: offset为0时会导致lowbit函数死循环
**/
func numberOfPairs(nums1 []int, nums2 []int, diff int) int64 {
	var n = len(nums1)
	var tree = TreeArr{arr: make([]int, 1e5)}
	var res int64 = 0
	for i := 0; i < n; i++ {
		var offset = nums1[i] - nums2[i] + int(2e4+10)
		var cur = int64(tree.query(offset + diff))
		res += cur
		tree.update(offset, 1)
	}
	return res
}

type TreeArr struct {
	arr []int
}

func (t TreeArr) query(i int) int {
	var res = 0
	for i > 0 {
		res += t.arr[i]
		i = i - t.lowbit(i)
	}
	return res
}

func (t TreeArr) update(i int, val int) {
	var n = len(t.arr)
	for i <= n {
		t.arr[i] = t.arr[i] + val
		i = i + t.lowbit(i)
	}
}

// x最低位1的位置
func (t TreeArr) lowbit(x int) int {
	return x & -x
}

